mapper.select:
SELECT COUNT(*)
FROM member
WHERE memType = #{memType}

mapper.update:
UPDATE member
SET status = #{status}, loss_count = 0
WHERE memId = #{memId}

UPDATE member
SET memType = #{memType}
WHERE memId = #{memId}

mapper.select:
SELECT title, content, createdDate, boardCtgId, memId, memNickname, postId, status FROM ( SELECT b.title, b.content, b.createdDate, b.boardCtgId, b.memId, m.memNickname, b.boardPostNo AS postId, b.status FROM board b JOIN member m ON b.memId = m.memId WHERE b.createdDate BETWEEN #{startDate} AND DATE_ADD(#{endDate}, INTERVAL 1 DAY)
AND b.boardCtgId = #{boardCategory}
UNION ALL
SELECT r.title, r.content, r.createdDate, r.boardCtgId, r.memId, m.memNickname, r.recordNo AS postId, r.status FROM myFishRecords r JOIN member m ON r.memId = m.memId WHERE r.createdDate BETWEEN #{startDate} AND DATE_ADD(#{endDate}, INTERVAL 1 DAY)
AND r.boardCtgId = #{boardCategory}
) AS combined ORDER BY createdDate DESC LIMIT #{size} OFFSET #{offset};

SELECT COUNT(*) FROM ( SELECT 1 FROM board WHERE createdDate BETWEEN #{startDate} AND DATE_ADD(#{endDate}, INTERVAL 1 DAY)
AND boardCtgId = #{boardCategory}
UNION ALL
SELECT 1 FROM myFishRecords WHERE createdDate BETWEEN #{startDate} AND DATE_ADD(#{endDate}, INTERVAL 1 DAY)
AND boardCtgId = #{boardCategory}
) AS total

mapper.update:
UPDATE board
SET status = #{status}
WHERE boardPostNo = #{boardPostNo};

UPDATE myfishrecords
SET status = #{status}
WHERE recordNo = #{recordNo};
