mapper.update:
UPDATE cart
SET cartQty = #{cartQty}
WHERE cartNo = #{cartNo}

mapper.select:
SELECT memId, memName, memHP, memZipcode, memAddress1, memAddress2
FROM member
WHERE memId = #{memId}

SELECT c.cartNo, c.memId, c.cartQty, p.prdNo, p.prdImage, p.prdName, p.prdPrice
FROM product p, cart c
WHERE p.prdNo = c.prdNo AND c.cartNo = #{cartNo}

mapper.delete:
DELETE FROM cart 
WHERE cartNo IN

    #{cartNo}

AND memId = #{memId}

mapper.select:
SELECT op.ordNo, op.prdNo, op.ordQty, p.prdImage, p.prdName, p.prdPrice
FROM product p, order_product op
WHERE p.prdNo = op.prdNo  AND op.memId = #{memId}
ORDER BY op.ordNo DESC;

SELECT op.ordNo, op.prdNo, op.ordQty, p.prdImage, p.prdName, p.prdPrice
FROM product p, order_product op
WHERE p.prdNo = op.prdNo  AND op.memId = #{memId}
AND STR_TO_DATE(SUBSTRING(op.ordNo, 1, 8), '%Y%m%d') >= DATE_SUB(NOW(), INTERVAL 

    3 MONTH
    6 MONTH
    1 YEAR
    3 YEAR
)
ORDER BY op.ordNo DESC;
