		UPDATE myFishRecords
        SET title = #{title}, content = #{content}, uploadImage = #{uploadImage}, fishName = #{fishName}, fishSize = #{fishSize}, createdDate = #{createdDate}, equipment = #{equipment}, location = #{location}, weather = #{weather}
